package patches.buildTypes

import jetbrains.buildServer.configs.kotlin.*
import jetbrains.buildServer.configs.kotlin.buildFeatures.swabra
import jetbrains.buildServer.configs.kotlin.buildSteps.gradle
import jetbrains.buildServer.configs.kotlin.buildSteps.script
import jetbrains.buildServer.configs.kotlin.ui.*

/*
This patch script was generated by TeamCity on settings change in UI.
To apply the patch, change the buildType with id = 'Build'
accordingly, and delete the patch script.
*/
changeBuildType(RelativeId("Build")) {
    params {
        add {
            param("inherit", "meow?")
        }
    }

    vcs {
        add(RelativeId("HttpsGithubComSocksDevilTeamcityAwsLambdaPluginExampleRefsHeadsMain"))
    }

    expectSteps {
        script {
            id = "simpleRunner_1"
            scriptContent = """
                echo "##teamcity[setParameter name='TEST' value='test']"
                echo '%vaultParam%' > meow.txt
            """.trimIndent()
        }
        gradle {
            id = "gradle_runner"
            tasks = "clean build"
            gradleWrapperPath = ""
        }
        script {
            id = "simpleRunner"
            scriptContent = """
                echo '%vaultParam%' >> meow.txt
                echo '%TEST%' >> meow.txt
                #sleep 1000000
            """.trimIndent()
            param("teamcity.kubernetes.executor.pull.policy", "IfNotPresent")
        }
    }
    steps {
        insert(0) {
            gradle {
                id = "gradle_runner"
                tasks = "clean build"
                param("teamcity.kubernetes.executor.pull.policy", "IfNotPresent")
            }
        }
        items.removeAt(2)
    }

    features {
        add {
            swabra {
            }
        }
    }
}
