package patches.projects

import jetbrains.buildServer.configs.kotlin.*
import jetbrains.buildServer.configs.kotlin.Project
import jetbrains.buildServer.configs.kotlin.kubernetesCloudImage
import jetbrains.buildServer.configs.kotlin.projectFeatures.KubernetesExecutor
import jetbrains.buildServer.configs.kotlin.projectFeatures.kubernetesExecutor
import jetbrains.buildServer.configs.kotlin.ui.*

/*
This patch script was generated by TeamCity on settings change in UI.
To apply the patch, change the root project
accordingly, and delete the patch script.
*/
changeProject(DslContext.projectId) {
    features {
        remove {
            kubernetesCloudImage {
                id = "PROJECT_EXT_29"
                profileId = "kube-5"
                agentPoolId = "-2"
                agentNamePrefix = "linux"
                podSpecification = runContainer {
                    dockerImage = "jetbrains/teamcity-agent:2025.07"
                }
            }
        }
        val feature1 = find<KubernetesExecutor> {
            kubernetesExecutor {
                id = "PROJECT_EXT_30"
                connectionId = "PROJECT_EXT_3"
                profileName = "K8S Executor"
                enabled = true
            }
        }
        feature1.apply {
            templateName = "meow"
            param("enabled", "")
        }
        add {
            kubernetesCloudImage {
                id = "PROJECT_EXT_58"
                profileId = "kube-5"
                agentPoolId = "-2"
                agentNamePrefix = "linux"
                podSpecification = runContainer {
                    dockerImage = "jetbrains/teamcity-agent:2025.07"
                }
            }
        }
    }
}
